import { useEffect } from 'react';

import * as messageActions from '../dux/actionTypes';
import { uuidv4 } from '../../../utils/uuid';
import compareIds from '../../../utils/compareIds';
import { scrollIntoLast } from '../utils';

/**
 * Handles ChannelEvents and send values to dispatcher using messagesDispatcher
 * messagesDispatcher: Dispatcher
 * sdk: sdkInstance
 * logger: loggerInstance
 * channelUrl: string
 * sdkInit: bool
 */
function useHandleChannelEvents({ currentGroupChannel, sdkInit, hasMoreToBottom }, {
  messagesDispatcher,
  sdk,
  logger,
  scrollRef,
}) {
  const channelUrl = currentGroupChannel && currentGroupChannel.url;
  useEffect(() => {
    const messageReceiverId = uuidv4();
    if (channelUrl && sdk && sdk.ChannelHandler) {
      const ChannelHandler = new sdk.ChannelHandler();
      logger.info('Channel | useHandleChannelEvents: Setup event handler', messageReceiverId);
      ChannelHandler.onMessageReceived = (channel, message) => {
        // donot update if hasMoreToBottom
        if (compareIds(channel.url, currentGroupChannel.url) && !hasMoreToBottom) {
          let scrollToEnd = false;
          try {
            const { current } = scrollRef;
            scrollToEnd = current.offsetHeight + current.scrollTop >= current.scrollHeight;
          } catch (error) {
            //
          }

          logger.info('Channel | useHandleChannelEvents: onMessageReceived', message);
          messagesDispatcher({
            type: messageActions.ON_MESSAGE_RECEIVED,
            payload: { channel, message, scrollToEnd },
          });

          if (scrollToEnd) {
            try {
              setTimeout(() => {
                currentGroupChannel.markAsRead();
                scrollIntoLast();
              });
            } catch (error) {
              logger.warning('Channel | onMessageReceived | scroll to end failed');
            }
          }
        }
        if (compareIds(channel.url, currentGroupChannel.url) && hasMoreToBottom) {
          messagesDispatcher({
            type: messageActions.UPDATE_UNREAD_COUNT,
            payload: { channel },
          });
        }
      };

      ChannelHandler.onMessageUpdated = (channel, message) => {
        logger.info('Channel | useHandleChannelEvents: onMessageUpdated', message);
        messagesDispatcher({
          type: messageActions.ON_MESSAGE_UPDATED,
          payload: { channel, message },
        });
      };

      ChannelHandler.onMessageDeleted = (_, messageId) => {
        logger.info('Channel | useHandleChannelEvents: onMessageDeleted', messageId);
        messagesDispatcher({
          type: messageActions.ON_MESSAGE_DELETED,
          payload: messageId,
        });
      };

      ChannelHandler.onReactionUpdated = (_, reactionEvent) => {
        logger.info('Channel | useHandleChannelEvents: onReactionUpdated', reactionEvent);
        messagesDispatcher({
          type: messageActions.ON_REACTION_UPDATED,
          payload: reactionEvent,
        });
      };

      ChannelHandler.onChannelChanged = (groupChannel) => {
        if (compareIds(groupChannel.url, currentGroupChannel.url)) {
          logger.info('Channel | useHandleChannelEvents: onChannelChanged', groupChannel);
          messagesDispatcher({
            type: messageActions.SET_CURRENT_CHANNEL,
            payload: groupChannel,
          });
        }
      };

      ChannelHandler.onChannelFrozen = (groupChannel) => {
        if (compareIds(groupChannel.url, currentGroupChannel.url)) {
          logger.info('Channel | useHandleChannelEvents: onChannelFrozen', groupChannel);
          messagesDispatcher({
            type: messageActions.SET_CURRENT_CHANNEL,
            payload: groupChannel,
          });
        }
      };

      ChannelHandler.onChannelUnfrozen = (groupChannel) => {
        if (compareIds(groupChannel.url, currentGroupChannel.url)) {
          logger.info('Channel | useHandleChannelEvents: onChannelUnFrozen', groupChannel);
          messagesDispatcher({
            type: messageActions.SET_CURRENT_CHANNEL,
            payload: groupChannel,
          });
        }
      };

      ChannelHandler.onUserMuted = (groupChannel) => {
        if (compareIds(groupChannel.url, currentGroupChannel.url)) {
          logger.info('Channel | useHandleChannelEvents: onUserMuted', groupChannel);
          messagesDispatcher({
            type: messageActions.SET_CURRENT_CHANNEL,
            payload: groupChannel,
          });
        }
      };

      ChannelHandler.onUserUnmuted = (groupChannel) => {
        if (compareIds(groupChannel.url, currentGroupChannel.url)) {
          logger.info('Channel | useHandleChannelEvents: onUserUnmuted', groupChannel);
          messagesDispatcher({
            type: messageActions.SET_CURRENT_CHANNEL,
            payload: groupChannel,
          });
        }
      };

      ChannelHandler.onUserBanned = (groupChannel) => {
        if (compareIds(groupChannel.url, currentGroupChannel.url)) {
          logger.info('Channel | useHandleChannelEvents: onUserBanned', groupChannel);
          messagesDispatcher({
            type: messageActions.SET_CURRENT_CHANNEL,
            payload: groupChannel,
          });
        }
      };

      ChannelHandler.onOperatorUpdated = (groupChannel) => {
        if (compareIds(groupChannel.url, currentGroupChannel.url)) {
          logger.info('Channel | useHandleChannelEvents: onOperatorUpdated', groupChannel);
          messagesDispatcher({
            type: messageActions.SET_CURRENT_CHANNEL,
            payload: groupChannel,
          });
        }
      };

      // Add this channel event handler to the SendBird object.
      sdk.addChannelHandler(messageReceiverId, ChannelHandler);
    }
    return () => {
      if (sdk && sdk.removeChannelHandler) {
        logger.info('Channel | useHandleChannelEvents: Removing message reciver handler', messageReceiverId);
        sdk.removeChannelHandler(messageReceiverId);
      }
    };
  }, [channelUrl, sdkInit]);
}

export default useHandleChannelEvents;

import { useEffect } from 'react';

import * as messageActionTypes from '../dux/actionTypes';

function useSetChannel({ channelUrl, sdkInit }, {
  messagesDispatcher,
  sdk,
  logger,
}) {
  useEffect(() => {
    if (channelUrl && sdkInit && sdk && sdk.GroupChannel) {
      logger.info('Channel | useSetChannel fetching channel', channelUrl);
      sdk.GroupChannel.getChannel(channelUrl)
        .then((groupChannel) => {
          logger.info('Channel | useSetChannel fetched channel', groupChannel);
          messagesDispatcher({
            type: messageActionTypes.SET_CURRENT_CHANNEL,
            payload: groupChannel,
          });

          logger.info('Channel: Mark as read', groupChannel);
          // this order is important - this mark as read should update the event handler up above
          groupChannel.markAsRead();
        })
        .catch((e) => {
          logger.warning('Channel | useSetChannel fetch channel failed', { channelUrl, e });
          messagesDispatcher({
            type: messageActionTypes.SET_CHANNEL_INVALID,
          });
        });
      sdk.getAllEmoji((emojiContainer_, err) => {
        if (err) {
          logger.error('Channel: Getting emojis failed', err);
          return;
        }
        logger.info('Channel: Getting emojis success', emojiContainer_);
        messagesDispatcher({
          type: messageActionTypes.SET_EMOJI_CONTAINER,
          payload: emojiContainer_,
        });
      });
    }
  }, [channelUrl, sdkInit]);
}

export default useSetChannel;

import { useEffect } from 'react';

import * as messageActionTypes from '../dux/actionTypes';
import * as utils from '../utils';

const PREV_RESULT_SIZE = 30;
const NEXT_RESULT_SIZE = 10;

const getLatestMessageTimeStamp = (messages = []) => {
  const latestMessage = messages[messages.length - 1];
  return (latestMessage && latestMessage.createdAt) || null;
};

function useInitialMessagesFetch({
  currentGroupChannel,
  userFilledMessageListQuery,
  intialTimeStamp,
}, {
  sdk,
  logger,
  messagesDispatcher,
}) {
  const channelUrl = currentGroupChannel && currentGroupChannel.url;
  useEffect(() => {
    logger.info('Channel useInitialMessagesFetch: Setup started', currentGroupChannel);
    messagesDispatcher({
      type: messageActionTypes.RESET_MESSAGES,
    });

    if (sdk && sdk.MessageListParams
      && currentGroupChannel && currentGroupChannel.getMessagesByMessageId) {
      const messageListParams = new sdk.MessageListParams();
      messageListParams.prevResultSize = PREV_RESULT_SIZE;
      messageListParams.isInclusive = true;
      messageListParams.includeReplies = false;
      messageListParams.includeReaction = true;
      if (userFilledMessageListQuery) {
        Object.keys(userFilledMessageListQuery).forEach((key) => {
          messageListParams[key] = userFilledMessageListQuery[key];
        });
      }
      logger.info('Channel: Fetching messages', { currentGroupChannel, userFilledMessageListQuery });
      messagesDispatcher({
        type: messageActionTypes.GET_PREV_MESSAGES_START,
      });

      if (intialTimeStamp) {
        messageListParams.nextResultSize = NEXT_RESULT_SIZE;
        currentGroupChannel.getMessagesByTimestamp(
          intialTimeStamp,
          messageListParams,
        )
          .then((messages) => {
            const hasMore = (messages && messages.length > 0);
            const lastMessageTimeStamp = hasMore
              ? messages[0].createdAt
              : null;
            const latestFetchedMessageTimeStamp = getLatestMessageTimeStamp(messages);
            // to make sure there are no more messages below
            const nextMessageListParams = new sdk.MessageListParams();
            nextMessageListParams.nextResultSize = NEXT_RESULT_SIZE;
            currentGroupChannel.getMessagesByTimestamp(
              latestFetchedMessageTimeStamp || new Date().getTime(),
              nextMessageListParams,
            ).then((nextMessages) => {
              messagesDispatcher({
                type: messageActionTypes.GET_PREV_MESSAGES_SUCESS,
                payload: {
                  messages,
                  hasMore,
                  lastMessageTimeStamp,
                  currentGroupChannel,
                  latestFetchedMessageTimeStamp,
                  hasMoreToBottom: nextMessages && nextMessages.length > 0,
                },
              });
            });
          })
          .catch((error) => {
            logger.error('Channel: Fetching messages failed', error);
            messagesDispatcher({
              type: messageActionTypes.GET_PREV_MESSAGES_SUCESS,
              payload: {
                messages: [],
                hasMore: false,
                lastMessageTimeStamp: 0,
                currentGroupChannel,
              },
            });
          })
          .finally(() => {
            if (!intialTimeStamp) {
              setTimeout(() => utils.scrollIntoLast());
            }
            currentGroupChannel.markAsRead();
          });
      } else {
        currentGroupChannel.getMessagesByTimestamp(
          new Date().getTime(),
          messageListParams,
        )
          .then((messages) => {
            const hasMore = (messages && messages.length > 0);
            const lastMessageTimeStamp = hasMore
              ? messages[0].createdAt
              : null;
            const latestFetchedMessageTimeStamp = getLatestMessageTimeStamp(messages);
            messagesDispatcher({
              type: messageActionTypes.GET_PREV_MESSAGES_SUCESS,
              payload: {
                messages,
                hasMore,
                lastMessageTimeStamp,
                currentGroupChannel,
                latestFetchedMessageTimeStamp,
                hasMoreToBottom: false,
              },
            });
          })
          .catch((error) => {
            logger.error('Channel: Fetching messages failed', error);
            messagesDispatcher({
              type: messageActionTypes.GET_PREV_MESSAGES_SUCESS,
              payload: {
                messages: [],
                hasMore: false,
                lastMessageTimeStamp: 0,
                currentGroupChannel,
              },
            });
          })
          .finally(() => {
            if (!intialTimeStamp) {
              setTimeout(() => utils.scrollIntoLast());
            }
            currentGroupChannel.markAsRead();
          });
      }
    }
  }, [channelUrl, userFilledMessageListQuery, intialTimeStamp]);
}

export default useInitialMessagesFetch;

import { useEffect } from 'react';

import * as utils from '../utils';
import * as messageActionTypes from '../dux/actionTypes';

function useHandleReconnect({ isOnline }, {
  logger,
  sdk,
  currentGroupChannel,
  messagesDispatcher,
  userFilledMessageListQuery,
}) {
  useEffect(() => {
    const wasOffline = !isOnline;
    return () => {
      // state changed from offline to online
      if (wasOffline) {
        logger.info('Refreshing conversation state');
        const { appInfo = {} } = sdk;
        const useReaction = appInfo.isUsingReaction || false;

        const messageListParams = new sdk.MessageListParams();
        messageListParams.prevResultSize = 30;
        messageListParams.includeReplies = false;
        messageListParams.includeReaction = useReaction;

        if (userFilledMessageListQuery) {
          Object.keys(userFilledMessageListQuery).forEach((key) => {
            messageListParams[key] = userFilledMessageListQuery[key];
          });
        }
        logger.info('Channel: Fetching messages', { currentGroupChannel, userFilledMessageListQuery });
        messagesDispatcher({
          type: messageActionTypes.GET_PREV_MESSAGES_START,
        });

        sdk.GroupChannel.getChannel(currentGroupChannel.url)
          .then((groupChannel) => {
            const lastMessageTime = new Date().getTime();

            groupChannel.getMessagesByTimestamp(
              lastMessageTime,
              messageListParams,
            )
              .then((messages) => {
                messagesDispatcher({
                  type: messageActionTypes.CLEAR_SENT_MESSAGES,
                });

                const hasMore = (messages && messages.length > 0);
                const lastMessageTimeStamp = hasMore
                  ? messages[0].createdAt
                  : null;
                messagesDispatcher({
                  type: messageActionTypes.GET_PREV_MESSAGES_SUCESS,
                  payload: {
                    messages,
                    hasMore,
                    lastMessageTimeStamp,
                    currentGroupChannel,
                  },
                });
                setTimeout(() => utils.scrollIntoLast());
              })
              .catch((error) => {
                logger.error('Channel: Fetching messages failed', error);
              })
              .finally(() => {
                currentGroupChannel.markAsRead();
              });
          });
      }
    };
  }, [isOnline]);
}

export default useHandleReconnect;

import { useCallback } from 'react';

import * as messageActionTypes from '../dux/actionTypes';

function useScrollCallback({
  currentGroupChannel,
  lastMessageTimeStamp,
  userFilledMessageListQuery,
}, {
  hasMore,
  logger,
  messagesDispatcher,
  sdk,
}) {
  return useCallback((cb) => {
    if (!hasMore) { return; }
    const messageListParams = new sdk.MessageListParams();
    messageListParams.prevResultSize = 30;
    messageListParams.includeReplies = false;
    messageListParams.includeReaction = true;

    if (userFilledMessageListQuery) {
      Object.keys(userFilledMessageListQuery).forEach((key) => {
        messageListParams[key] = userFilledMessageListQuery[key];
      });
    }
    logger.info('Channel: Fetching messages', { currentGroupChannel, userFilledMessageListQuery });

    currentGroupChannel.getMessagesByTimestamp(
      lastMessageTimeStamp || new Date().getTime(),
      messageListParams,
    )
      .then((messages) => {
        const hasMoreMessages = (messages && messages.length > 0);
        const lastMessageTs = hasMoreMessages
          ? messages[0].createdAt
          : null;

        messagesDispatcher({
          type: messageActionTypes.GET_PREV_MESSAGES_SUCESS,
          payload: {
            messages,
            hasMore: hasMoreMessages,
            lastMessageTimeStamp: lastMessageTs,
            currentGroupChannel,
          },
        });
        cb([messages, null]);
      })
      .catch((error) => {
        logger.error('Channel: Fetching messages failed', error);
        messagesDispatcher({
          type: messageActionTypes.GET_PREV_MESSAGES_SUCESS,
          payload: {
            messages: [],
            hasMore: false,
            lastMessageTimeStamp: 0,
            currentGroupChannel,
          },
        });
        cb([null, error]);
      })
      .finally(() => {
        currentGroupChannel.markAsRead();
      });
  }, [currentGroupChannel, lastMessageTimeStamp]);
}

export default useScrollCallback;

import { useCallback } from 'react';

import * as messageActionTypes from '../dux/actionTypes';

const RESULT_SIZE = 30;

function useScrollDownCallback({
  currentGroupChannel,
  latestFetchedMessageTimeStamp,
  userFilledMessageListQuery,
  hasMoreToBottom,
}, {
  logger,
  messagesDispatcher,
  sdk,
}) {
  return useCallback((cb) => {
    if (!hasMoreToBottom) { return; }
    const messageListParams = new sdk.MessageListParams();
    messageListParams.nextResultSize = RESULT_SIZE;
    messageListParams.includeReplies = false;
    messageListParams.includeReaction = true;

    if (userFilledMessageListQuery) {
      Object.keys(userFilledMessageListQuery).forEach((key) => {
        messageListParams[key] = userFilledMessageListQuery[key];
      });
    }
    logger.info('Channel: Fetching later messages', { currentGroupChannel, userFilledMessageListQuery });

    currentGroupChannel.getMessagesByTimestamp(
      latestFetchedMessageTimeStamp || new Date().getTime(),
      messageListParams,
    )
      .then((messages) => {
        const messagesLength = (messages && messages.length) || 0;
        const hasMoreMessages = (messagesLength > 0)
          && (messageListParams.nextResultSize === messagesLength);
        const lastMessageTs = hasMoreMessages
          ? messages[messages.length - 1].createdAt
          : null;
        messagesDispatcher({
          type: messageActionTypes.GET_NEXT_MESSAGES_SUCESS,
          payload: {
            messages,
            hasMoreToBottom: hasMoreMessages,
            latestFetchedMessageTimeStamp: lastMessageTs,
            currentGroupChannel,
          },
        });
        cb([messages, null]);
      })
      .catch((error) => {
        logger.error('Channel: Fetching later messages failed', error);
        messagesDispatcher({
          type: messageActionTypes.GET_NEXT_MESSAGES_FAILURE,
          payload: {
            messages: [],
            hasMoreToBottom: false,
            latestFetchedMessageTimeStamp: 0,
            currentGroupChannel,
          },
        });
        cb([null, error]);
      })
      .finally(() => {
        currentGroupChannel.markAsRead();
      });
  }, [currentGroupChannel, latestFetchedMessageTimeStamp, hasMoreToBottom]);
}

export default useScrollDownCallback;

import { useCallback } from 'react';

import * as messageActionTypes from '../dux/actionTypes';

function useDeleteMessageCallback({ currentGroupChannel, messagesDispatcher }, { logger }) {
  return useCallback((message, cb) => {
    logger.info('Channel | useDeleteMessageCallback: Deleting message', message);
    const { requestState } = message;
    logger.info('Channel | useDeleteMessageCallback: Deleting message requestState:', requestState);

    // Message is only on local
    if (requestState === 'failed' || requestState === 'pending') {
      logger.info('Channel | useDeleteMessageCallback: Deleted message from local:', message);
      messagesDispatcher({
        type: messageActionTypes.ON_MESSAGE_DELETED_BY_REQ_ID,
        payload: message.reqId,
      });
      if (cb) { cb(); }
      return;
    }

    // Message is on server
    currentGroupChannel.deleteMessage(message, (err) => {
      logger.info('Channel | useDeleteMessageCallback: Deleting message from remote:', requestState);
      if (cb) { cb(err); }
      if (!err) {
        logger.info('Channel | useDeleteMessageCallback: Deleting message success!', message);
        messagesDispatcher({
          type: messageActionTypes.ON_MESSAGE_DELETED,
          payload: message.messageId,
        });
      } else {
        logger.warning('Channel | useDeleteMessageCallback: Deleting message failed!', err);
      }
    });
  }, [currentGroupChannel, messagesDispatcher]);
}

export default useDeleteMessageCallback;

import { useCallback } from 'react';

import * as messageActionTypes from '../dux/actionTypes';
import * as topics from '../../../lib/pubSub/topics';

function useUpdateMessageCallback({
  currentGroupChannel,
  messagesDispatcher,
  onBeforeUpdateUserMessage,
}, {
  logger,
  pubSub,
  sdk,
}) {
  return useCallback((messageId, text, cb) => {
    const createParamsDefault = (txt) => {
      const params = new sdk.UserMessageParams();
      params.message = txt;
      return params;
    };

    const createCustomPrams = onBeforeUpdateUserMessage
      && typeof onBeforeUpdateUserMessage === 'function';

    if (createCustomPrams) {
      logger.info('Channel: creating params using onBeforeUpdateUserMessage', onBeforeUpdateUserMessage);
    }

    const params = onBeforeUpdateUserMessage
      ? onBeforeUpdateUserMessage(text)
      : createParamsDefault(text);

    currentGroupChannel.updateUserMessage(messageId, params, (r, e) => {
      logger.info('Channel: Updating message!', params);
      const swapParams = sdk.getErrorFirstCallback();
      let message = r;
      let err = e;
      if (swapParams) {
        message = e;
        err = r;
      }
      if (cb) {
        cb(err, message);
      }
      if (!err) {
        logger.info('Channel: Updating message success!', message);
        messagesDispatcher({
          type: messageActionTypes.ON_MESSAGE_UPDATED,
          payload: {
            channel: currentGroupChannel,
            message,
          },
        });
        pubSub.publish(
          topics.UPDATE_USER_MESSAGE,
          {
            message,
            channel: currentGroupChannel,
          },
        );
      } else {
        logger.warning('Channel: Updating message failed!', err);
      }
    });
  }, [currentGroupChannel.url, messagesDispatcher, onBeforeUpdateUserMessage]);
}

export default useUpdateMessageCallback;

import { useCallback } from 'react';

import * as messageActionTypes from '../dux/actionTypes';

function useResendMessageCallback({
  currentGroupChannel,
  messagesDispatcher,
}, {
  logger,
}) {
  return useCallback((failedMessage) => {
    logger.info('Channel: Resending message has started', failedMessage);
    const { messageType, file } = failedMessage;
    if (failedMessage && typeof failedMessage.isResendable === 'function'
      && failedMessage.isResendable()
    ) {
      // eslint-disable-next-line no-param-reassign
      failedMessage.requestState = 'pending';
      messagesDispatcher({
        type: messageActionTypes.RESEND_MESSAGEGE_START,
        payload: failedMessage,
      });

      // userMessage
      if (messageType === 'user') {
        currentGroupChannel
          .resendUserMessage(failedMessage)
          .then((message) => {
            logger.info('Channel: Resending message success!', { message });
            messagesDispatcher({
              type: messageActionTypes.SEND_MESSAGEGE_SUCESS,
              payload: message,
            });
          })
          .catch((e) => {
            logger.warning('Channel: Resending message failed!', { e });
            // eslint-disable-next-line no-param-reassign
            failedMessage.requestState = 'failed';
            messagesDispatcher({
              type: messageActionTypes.SEND_MESSAGEGE_FAILURE,
              payload: failedMessage,
            });
          });

        // eslint-disable-next-line no-param-reassign
        failedMessage.requestState = 'pending';
        messagesDispatcher({
          type: messageActionTypes.RESEND_MESSAGEGE_START,
          payload: failedMessage,
        });
        return;
      }

      if (messageType === 'file') {
        currentGroupChannel
          .resendFileMessage(failedMessage, file)
          .then((message) => {
            logger.info('Channel: Resending file message success!', { message });
            messagesDispatcher({
              type: messageActionTypes.SEND_MESSAGEGE_SUCESS,
              payload: message,
            });
          })
          .catch((e) => {
            logger.warning('Channel: Resending file message failed!', { e });
            // eslint-disable-next-line no-param-reassign
            failedMessage.requestState = 'failed';
            messagesDispatcher({
              type: messageActionTypes.SEND_MESSAGEGE_FAILURE,
              payload: failedMessage,
            });
          });

        // eslint-disable-next-line no-param-reassign
        failedMessage.requestState = 'pending';
        messagesDispatcher({
          type: messageActionTypes.RESEND_MESSAGEGE_START,
          payload: failedMessage,
        });
      }
    } else {
      // to alert user on console
      // eslint-disable-next-line no-console
      console.error('Message is not resendable');
      logger.warning('Message is not resendable', failedMessage);
    }
  }, [currentGroupChannel, messagesDispatcher]);
}

export default useResendMessageCallback;

import { useRef, useCallback } from 'react';

import * as messageActionTypes from '../dux/actionTypes';
import * as utils from '../utils';
import * as topics from '../../../lib/pubSub/topics';

export default function useSendMessageCallback({ currentGroupChannel, onBeforeSendUserMessage }, {
  sdk,
  logger,
  pubSub,
  messagesDispatcher,
}) {
  const messageInputRef = useRef(null);

  const sendMessage = useCallback(
    () => {
      const text = messageInputRef.current.value;
      const createParamsDefault = (txt) => {
        const message = (typeof txt === 'string') ? txt.trim() : txt;
        const params = new sdk.UserMessageParams();
        params.message = message;
        return params;
      };

      const createCustomPrams = onBeforeSendUserMessage
        && typeof onBeforeSendUserMessage === 'function';

      if (createCustomPrams) {
        logger.info('Channel: creating params using onBeforeSendUserMessage', onBeforeSendUserMessage);
      }

      const params = onBeforeSendUserMessage
        ? onBeforeSendUserMessage(text)
        : createParamsDefault(text);

      logger.info('Channel: Sending message has started', params);
      const pendingMsg = currentGroupChannel.sendUserMessage(params, (res, err) => {
        const swapParams = sdk.getErrorFirstCallback();
        let message = res;
        let error = err;
        if (swapParams) {
          message = err;
          error = res;
        }
        // sending params instead of pending message
        // to make sure that we can resend the message once it fails
        if (error) {
          logger.warning('Channel: Sending message failed!', {
            message,
          });
          messagesDispatcher({
            type: messageActionTypes.SEND_MESSAGEGE_FAILURE,
            payload: message,
          });
          return;
        }
        logger.info('Channel: Sending message success!', message);
        messagesDispatcher({
          type: messageActionTypes.SEND_MESSAGEGE_SUCESS,
          payload: message,
        });
      });
      pubSub.publish(topics.SEND_MESSAGE_START, {
        /* pubSub is used instead of messagesDispatcher
          to avoid redundantly calling `messageActionTypes.SEND_MESSAGEGE_START` */
        message: pendingMsg,
        channel: currentGroupChannel,
      });
      setTimeout(() => utils.scrollIntoLast());
    },
    [currentGroupChannel, onBeforeSendUserMessage],
  );

  return [messageInputRef, sendMessage];
}

import { useCallback } from 'react';

import * as messageActionTypes from '../dux/actionTypes';
import * as utils from '../utils';
import * as topics from '../../../lib/pubSub/topics';

export default function useSendFileMessageCallback({
  currentGroupChannel,
  onBeforeSendFileMessage,
  imageCompression = {},
}, {
  sdk,
  logger,
  pubSub,
  messagesDispatcher,
}) {
  const sendMessage = useCallback((file) => {
    const {
      compressionRate,
      resizingWidth,
      resizingHeight,
    } = imageCompression;
    const createCustomParams = onBeforeSendFileMessage && typeof onBeforeSendFileMessage === 'function';

    const compressibleFileType = (file.type === 'image/jpg' || file.type === 'image/png' || file.type === 'image/jpeg');
    const compressibleRatio = (compressionRate > 0) && (compressionRate < 1);
    // pxToNumber returns null if values are invalid
    const compressibleDiamensions = utils.pxToNumber(resizingWidth)
      || utils.pxToNumber(resizingHeight);

    const canCompressImage = compressibleFileType && (compressibleRatio || compressibleDiamensions);

    const createParamsDefault = (file_) => {
      const params = new sdk.FileMessageParams();
      params.file = file_;
      return params;
    };

    if (canCompressImage) { // Using image compression
      try {
        const image = document.createElement('img');
        image.src = URL.createObjectURL(file);
        image.onload = () => {
          URL.revokeObjectURL(image.src);
          const canvas = document.createElement('canvas');
          const imageWdith = image.naturalWidth || image.width;
          const imageHeight = image.naturalHeight || image.height;

          let targetWidth = utils.pxToNumber(resizingWidth) || imageWdith;
          let targetHeight = utils.pxToNumber(resizingHeight) || imageHeight;

          // In canvas.toBlob(callback, mimeType, qualityArgument)
          // qualityArgument doesnt work
          // so in case compressibleDiamensions are not present, we use ratio
          if (file.type === 'image/png' && !compressibleDiamensions) {
            targetWidth *= compressionRate;
            targetHeight *= compressionRate;
          }

          canvas.width = targetWidth;
          canvas.height = targetHeight;
          const context = canvas.getContext('2d');
          context.drawImage(image, 0, 0, targetWidth, targetHeight);
          context.canvas.toBlob(
            (newImageBlob) => {
              const compressedFile = new File([newImageBlob], file.name, { type: file.type });
              if (createCustomParams) {
                logger.info('Channel: Creating params using onBeforeSendFileMessage', onBeforeSendFileMessage);
              }
              const params = createCustomParams
                ? onBeforeSendFileMessage(compressedFile)
                : createParamsDefault(compressedFile);
              logger.info('Channel: Uploading file message start!', params);
              const pendingMessage = currentGroupChannel.sendFileMessage(
                params,
                (response, err) => {
                  const swapParams = sdk.getErrorFirstCallback();
                  const [message, error] = swapParams ? [err, response] : [response, err];
                  if (error) {
                    // sending params instead of pending message
                    // to make sure that we can resend the message once it fails
                    logger.error('Channel: Sending file message failed!', message);
                    message.localUrl = URL.createObjectURL(compressedFile);
                    message.file = compressedFile;
                    messagesDispatcher({
                      type: messageActionTypes.SEND_MESSAGEGE_FAILURE,
                      payload: message,
                    });
                    return;
                  }
                  logger.info('Channel: Sending file message success!', message);
                  messagesDispatcher({
                    type: messageActionTypes.SEND_MESSAGEGE_SUCESS,
                    payload: message,
                  });
                },
              );
              pubSub.publish(topics.SEND_MESSAGE_START, {
                /* pubSub is used instead of messagesDispatcher
                  to avoid redundantly calling `messageActionTypes.SEND_MESSAGEGE_START` */
                message: {
                  ...pendingMessage,
                  url: URL.createObjectURL(compressedFile),
                  // pending thumbnail message seems to be failed
                  requestState: 'pending',
                },
                channel: currentGroupChannel,
              });
              setTimeout(() => utils.scrollIntoLast(), 1000);
            },
            file.type,
            compressionRate,
          );
        };
      } catch (error) {
        logger.error('Channel: Sending file message failed!');
      }
    } else { // Not using image compression
      if (createCustomParams) {
        logger.info('Channel: creating params using onBeforeSendFileMessage', onBeforeSendFileMessage);
      }
      const params = onBeforeSendFileMessage
        ? onBeforeSendFileMessage(file)
        : createParamsDefault(file);
      logger.info('Channel: Uploading file message start!', params);

      const pendingMsg = currentGroupChannel.sendFileMessage(params, (response, err) => {
        const swapParams = sdk.getErrorFirstCallback();
        const [message, error] = swapParams ? [err, response] : [response, err];
        if (error) {
          // sending params instead of pending message
          // to make sure that we can resend the message once it fails
          logger.error('Channel: Sending file message failed!', message);
          message.localUrl = URL.createObjectURL(file);
          message.file = file;
          messagesDispatcher({
            type: messageActionTypes.SEND_MESSAGEGE_FAILURE,
            payload: message,
          });
          return;
        }
        logger.info('Channel: Sending message success!', message);
        messagesDispatcher({
          type: messageActionTypes.SEND_MESSAGEGE_SUCESS,
          payload: message,
        });
      });
      pubSub.publish(topics.SEND_MESSAGE_START, {
        /* pubSub is used instead of messagesDispatcher
          to avoid redundantly calling `messageActionTypes.SEND_MESSAGEGE_START` */
        message: {
          ...pendingMsg,
          url: URL.createObjectURL(file),
          // pending thumbnail message seems to be failed
          requestState: 'pending',
        },
        channel: currentGroupChannel,
      });
      setTimeout(() => utils.scrollIntoLast(), 1000);
    }
  }, [currentGroupChannel, onBeforeSendFileMessage, imageCompression]);
  return [sendMessage];
}

import { useEffect } from 'react';

import * as utils from '../utils';
import * as messageActionTypes from '../dux/actionTypes';
import { uuidv4 } from '../../../utils/uuid';

export default function useSetReadStatus({ allMessages, currentGroupChannel }, {
  messagesDispatcher,
  sdk,
  logger,
}) {
  useEffect(() => {
    if (!sdk.ChannelHandler || !currentGroupChannel.url) {
      return () => {};
    }
    // todo: move to reducer?
    const setReadStatus = () => {
      const allReadStatus = allMessages.reduce((accumulator, msg) => {
        if (msg.messageId !== 0) {
          return {
            ...accumulator,
            [msg.messageId]: utils.getParsedStatus(msg, currentGroupChannel),
          };
        }
        return accumulator;
      }, {});
      messagesDispatcher({
        type: messageActionTypes.SET_READ_STATUS,
        payload: allReadStatus,
      });
    };
    if (allMessages.length > 0) {
      setReadStatus();
    }

    const channelUrl = currentGroupChannel.url;
    const handler = new sdk.ChannelHandler();
    const handleMessageStatus = (c) => {
      if (channelUrl === c.url) {
        setReadStatus();
      }
    };
    handler.onDeliveryReceiptUpdated = handleMessageStatus;
    handler.onReadReceiptUpdated = handleMessageStatus;
    // Add this channel event handler to the SendBird object.
    const handlerId = uuidv4();
    logger.info('Channel | useSetReadStatus: Removing message reciver handler', handlerId);
    sdk.addChannelHandler(handlerId, handler);
    return () => {
      if (sdk && sdk.removeChannelHandler) {
        logger.info('Channel | useSetReadStatus: Removing message reciver handler', handlerId);
        sdk.removeChannelHandler(handlerId);
      }
    };
  }, [allMessages, currentGroupChannel]);
}

