import format from 'date-fns/format';
import MessageStatusType from '../MessageStatus/type';

export const createUrlTester = (regexp) => (text) => regexp.test(text);

export const getIsSentFromStatus = (status) => (
  status === MessageStatusType.SENT
  || status === MessageStatusType.DELIVERED
  || status === MessageStatusType.READ
);

export const copyToClipboard = (text) => {
  try {
    if (window.clipboardData && window.clipboardData.setData) {
      // Internet Explorer-specific code path
      // to prevent textarea being shown while dialog is visible.
      return window.clipboardData.setData('Text', text);
    }
    if (document.queryCommandSupported && document.queryCommandSupported('copy')) {
      const textarea = document.createElement('textarea');
      textarea.textContent = text;
      textarea.style.position = 'fixed'; // Prevent scrolling to bottom of page in Microsoft Edge.
      document.body.appendChild(textarea);
      textarea.select();
      try {
        return document.execCommand('copy'); // Security exception may be thrown by some browsers.
      } catch (ex) {
        return false;
      } finally {
        document.body.removeChild(textarea);
      }
    }
    return false;
  } catch (err) {
    return err;
  }
};

export const getSenderProfileUrl = (message) => message.sender && message.sender.profileUrl;

export const getSenderName = (message) => (
  message.sender && (
    message.sender.friendName
    || message.sender.nickname
    || message.sender.userId
  )
);

export const getMessageCreatedAt = (message) => format(message.createdAt, 'p');

export const checkOGIsEnalbed = (message) => {
  const { ogMetaData } = message;
  if (!ogMetaData) {
    return false;
  }
  const { url } = ogMetaData;
  if (!url) {
    return false;
  }
  return true;
};

export default {
  getSenderName,
  createUrlTester,
  copyToClipboard,
  checkOGIsEnalbed,
  getMessageCreatedAt,
  getIsSentFromStatus,
  getSenderProfileUrl,
};

import React, { useMemo, useContext } from 'react';

import { LocalizationContext } from '../../lib/LocalizationContext';
import Label, { LabelTypography, LabelColors } from '../Label';
import LinkLabel from '../LinkLabel';
import uuidv4 from '../../utils/uuid';
import { createUrlTester } from './utils';
import { URL_REG } from './const';

const isUrl = createUrlTester(URL_REG);

export default function useMemoizedMessageText({
  message,
  updatedAt,
  className,
  incoming = false,
}) {
  const { stringSet } = useContext(LocalizationContext);
  const WORD_TYPOGRAPHY = LabelTypography.BODY_1;
  const WORD_COLOR = incoming ? LabelColors.ONBACKGROUND_1 : LabelColors.ONCONTENT_1;
  const EDITED_COLOR = incoming ? LabelColors.ONBACKGROUND_2 : LabelColors.ONCONTENT_2;
  return useMemo(() => () => {
    const splitMessage = message.split(' ');
    const matchedMessage = splitMessage
      .map((word) => (
        isUrl(word)
          ? (
            <LinkLabel
              key={uuidv4()}
              className={className}
              src={word}
              type={WORD_TYPOGRAPHY}
              color={WORD_COLOR}
            >
              {word}
            </LinkLabel>
          )
          : (
            <Label
              key={uuidv4()}
              className={className}
              type={WORD_TYPOGRAPHY}
              color={WORD_COLOR}
            >
              {word}
            </Label>
          )
      ));
    if (updatedAt > 0) {
      matchedMessage.push(
        <Label
          key={uuidv4()}
          className={className}
          type={WORD_TYPOGRAPHY}
          color={EDITED_COLOR}
        >
          {stringSet.MESSAGE_EDITED}
        </Label>,
      );
    }
    return matchedMessage;
  }, [message, updatedAt, className]);
}

