                    </div>
                  )}
                />
              )
            }
          </div>
          <div
            className={`${OUTGOING_OG_MESSAGE}__og-tag ${checkOGIsEnalbed(message) ? '' : `${OUTGOING_OG_MESSAGE}__og-tag--disabled`}`}
            onClick={openLink}
            onKeyDown={openLink}
            role="button"
            tabIndex={0}
          >
            {
              ogMetaData.title && (
                <div className={`${OUTGOING_OG_MESSAGE}__og-tag__title`}>
