import React, {
  useState,
  useRef,
  useMemo,
  useLayoutEffect,
} from 'react';
import PropTypes from 'prop-types';
import format from 'date-fns/format';

import Message from '../../../ui/Message';
import AdminMessage from '../../../ui/AdminMessage';
import ThumbnailMessage from '../../../ui/ThumbnailMessage';
import FileMessage from '../../../ui/FileMessage';
import DateSeparator from '../../../ui/DateSeparator';
import Label, { LabelTypography, LabelColors } from '../../../ui/Label';
import MessageInput from '../../../ui/MessageInput';
import FileViewer from '../../../ui/FileViewer';
import RemoveMessageModal from './RemoveMessage';
import UnknownMessage from '../../../ui/UnknownMessage';
import OGMessage from '../../../ui/OGMessage';

import { MessageTypes, getMessageType } from '../types';

export default function MessageHoc({
  message,
  userId,
  disabled,
  editDisabled,
  hasSeperator,
  deleteMessage,
  updateMessage,
  status,
  resendMessage,
  useReaction,
  chainTop,
  chainBottom,
  emojiAllMap,
  membersMap,
  highLightedMessageId,
  toggleReaction,
  memoizedEmojiListItems,
  renderCustomMessage,
  currentGroupChannel,
}) {
  const { sender = {} } = message;
  const [showEdit, setShowEdit] = useState(false);
  const [showRemove, setShowRemove] = useState(false);
  const [showFileViewer, setShowFileViewer] = useState(false);
  const [isAnimated, setIsAnimated] = useState(false);
  const editMessageInputRef = useRef(null);
  const useMessageScrollRef = useRef(null);

  useLayoutEffect(() => {
    if (highLightedMessageId === message.messageId) {
      if (useMessageScrollRef && useMessageScrollRef.current) {
        useMessageScrollRef.current.scrollIntoView({
          block: 'center',
          inline: 'center',
        });
        setTimeout(() => {
          setIsAnimated(true);
        }, 500);
      }
    } else {
      setIsAnimated(false);
    }
  }, [highLightedMessageId, useMessageScrollRef.current, message.messageId]);
  const RenderedMessage = useMemo(() => {
    if (renderCustomMessage) {
      return renderCustomMessage(message, currentGroupChannel, chainTop, chainBottom);
      // Let's change this to object type on next major version up
    }
    return null;
  }, [message, message.message, renderCustomMessage]);

  const isByMe = (userId === sender.userId)
    || (message.requestState === 'pending')
    || (message.requestState === 'failed');

  if (RenderedMessage) {
    return (
      <div
        ref={useMessageScrollRef}
        className={`
          sendbird-msg-hoc sendbird-msg--scroll-ref
          ${isAnimated ? 'sendbird-msg-hoc__highlighted' : ''}
        `}
      >
        {/* date-seperator */}
        {
          hasSeperator && (
            <DateSeparator>
              <Label type={LabelTypography.CAPTION_2} color={LabelColors.ONBACKGROUND_2}>
                {format(message.createdAt, 'MMMM dd, yyyy')}
              </Label>
            </DateSeparator>
          )
        }
        <RenderedMessage message={message} />
      </div>
    );
  }

  if (showEdit) {
    return (
      <MessageInput
        isEdit
        disabled={editDisabled}
        ref={editMessageInputRef}
        name={message.messageId}
        onSendMessage={updateMessage}
        onCancelEdit={() => { setShowEdit(false); }}
        value={message.message}
      />
    );
  }

  return (
    <div
      ref={useMessageScrollRef}
      className={`
        sendbird-msg-hoc sendbird-msg--scroll-ref
        ${isAnimated ? 'sendbird-msg-hoc__animated' : ''}
      `}
    >
      {/* date-seperator */}
      {
        hasSeperator && (
          <DateSeparator>
            <Label type={LabelTypography.CAPTION_2} color={LabelColors.ONBACKGROUND_2}>
              {format(message.createdAt, 'MMMM dd, yyyy')}
            </Label>
          </DateSeparator>
        )
      }
      {/* Message */}
      {
        {
          [MessageTypes.ADMIN]: <AdminMessage message={message} />,
          [MessageTypes.FILE]: (
            <FileMessage
              message={message}
              userId={userId}
              disabled={disabled}
              isByMe={isByMe}
              showRemove={setShowRemove}
              resendMessage={resendMessage}
              status={status}
              useReaction={useReaction}
              emojiAllMap={emojiAllMap}
              membersMap={membersMap}
              toggleReaction={toggleReaction}
              memoizedEmojiListItems={memoizedEmojiListItems}
              chainTop={chainTop}
              chainBottom={chainBottom}
            />
          ),
          [MessageTypes.OG]: (
            <OGMessage
              message={message}
              status={status}
              isByMe={isByMe}
              userId={userId}
              showEdit={setShowEdit}
              disabled={disabled}
              showRemove={setShowRemove}
              resendMessage={resendMessage}
              useReaction={useReaction}
              emojiAllMap={emojiAllMap}
              membersMap={membersMap}
              toggleReaction={toggleReaction}
              memoizedEmojiListItems={memoizedEmojiListItems}
              chainTop={chainTop}
              chainBottom={chainBottom}
            />
          ),
          [MessageTypes.THUMBNAIL]: (
            <ThumbnailMessage
              disabled={disabled}
              message={message}
              userId={userId}
              isByMe={isByMe}
              showRemove={setShowRemove}
              resendMessage={resendMessage}
              onClick={setShowFileViewer}
              status={status}
              useReaction={useReaction}
              emojiAllMap={emojiAllMap}
              membersMap={membersMap}
              toggleReaction={toggleReaction}
              memoizedEmojiListItems={memoizedEmojiListItems}
              chainTop={chainTop}
              chainBottom={chainBottom}
            />
          ),
          [MessageTypes.USER]: (
            <Message
              message={message}
              disabled={disabled}
              isByMe={isByMe}
              userId={userId}
              showEdit={setShowEdit}
              showRemove={setShowRemove}
              resendMessage={resendMessage}
              status={status}
              useReaction={useReaction}
              emojiAllMap={emojiAllMap}
              membersMap={membersMap}
              toggleReaction={toggleReaction}
              memoizedEmojiListItems={memoizedEmojiListItems}
              chainTop={chainTop}
              chainBottom={chainBottom}
            />
          ),
        }[getMessageType(message)]
      }
      {/* Modal */}
      {
        showRemove && (
          <RemoveMessageModal
            onCloseModal={() => setShowRemove(false)}
            onDeleteMessage={() => {
              deleteMessage(message);
            }}
          />
        )
      }
      {
        showFileViewer && (
          <FileViewer
            onClose={() => setShowFileViewer(false)}
            message={message}
            onDelete={() => {
              deleteMessage(message, () => {
                setShowFileViewer(false);
              });
            }}
            isByMe={isByMe}
          />
        )
      }
      {
        !((message.isFileMessage && message.isFileMessage()) || message.messageType === 'file')
        && !(message.isAdminMessage && message.isAdminMessage())
        && !(((message.isUserMessage && message.isUserMessage()) || message.messageType === 'user'))
        && !(showFileViewer)
        && (
          <UnknownMessage
            message={message}
            status={status}
            isByMe={isByMe}
            showRemove={setShowRemove}
            chainTop={chainTop}
            chainBottom={chainBottom}
          />
        )
      }
    </div>
  );
}

MessageHoc.propTypes = {
  userId: PropTypes.string,
  message: PropTypes.shape({
    isFileMessage: PropTypes.func,
    isAdminMessage: PropTypes.func,
    isUserMessage: PropTypes.func,
    isDateSeperator: PropTypes.func,
    // should be a number, but there's a bug in SDK shich returns string
    messageId: PropTypes.number,
    type: PropTypes.string,
    createdAt: PropTypes.number,
    message: PropTypes.string,
    requestState: PropTypes.string,
    messageType: PropTypes.string,
    sender: PropTypes.shape({ userId: PropTypes.string }),
    ogMetaData: PropTypes.shape({}),
  }),
  highLightedMessageId: PropTypes.oneOfType([
    PropTypes.string,
    PropTypes.number,
  ]),
  renderCustomMessage: PropTypes.func,
  currentGroupChannel: PropTypes.shape({}),
  hasSeperator: PropTypes.bool,
  disabled: PropTypes.bool,
  editDisabled: PropTypes.bool,
  deleteMessage: PropTypes.func.isRequired,
  updateMessage: PropTypes.func.isRequired,
  resendMessage: PropTypes.func.isRequired,
  status: PropTypes.string,
  useReaction: PropTypes.bool.isRequired,
  chainTop: PropTypes.bool.isRequired,
  chainBottom: PropTypes.bool.isRequired,
  emojiAllMap: PropTypes.instanceOf(Map).isRequired,
  membersMap: PropTypes.instanceOf(Map).isRequired,
  toggleReaction: PropTypes.func,
  memoizedEmojiListItems: PropTypes.func,
};

MessageHoc.defaultProps = {
  userId: '',
  editDisabled: false,
  renderCustomMessage: null,
  currentGroupChannel: {},
  message: {},
  hasSeperator: false,
  disabled: false,
  highLightedMessageId: null,
  status: '',
  toggleReaction: () => { },
  memoizedEmojiListItems: () => '',
};

import React, { Component } from 'react';
import PropTypes from 'prop-types';
import isSameDay from 'date-fns/isSameDay';

import './conversation-scroll.scss';
import * as messageActionTypes from '../dux/actionTypes';

import MessageHOC from './MessageHOC';
import {
  getParsedStatus,
  compareMessagesForGrouping,
} from '../utils';
import PlaceHolder, { PlaceHolderTypes } from '../../../ui/PlaceHolder';
import Icon, { IconTypes, IconColors } from '../../../ui/Icon';

export default class ConversationScroll extends Component {
  onScroll = (e) => {
    const {
      scrollRef,
      hasMore,
      messagesDispatcher,
      onScroll,
      onScrollDown,
      currentGroupChannel,
    } = this.props;

    const element = e.target;
    const {
      scrollTop,
      clientHeight,
      scrollHeight,
    } = element;
    if (scrollTop === 0) {
      if (!hasMore) {
        return;
      }
      const nodes = scrollRef.current.querySelectorAll('.sendbird-msg--scroll-ref');
      const first = nodes && nodes[0];
      onScroll(([messages]) => {
        if (messages) {
          // https://github.com/scabbiaza/react-scroll-position-on-updating-dom
          try {
            first.scrollIntoView();
          } catch (error) {
            //
          }
        }
      });
    }

    if (clientHeight + scrollTop === scrollHeight) {
      const nodes = scrollRef.current.querySelectorAll('.sendbird-msg--scroll-ref');
      const last = nodes && nodes[nodes.length - 1];
      onScrollDown(([messages]) => {
        if (messages) {
          // https://github.com/scabbiaza/react-scroll-position-on-updating-dom
          try {
            last.scrollIntoView();
          } catch (error) {
            //
          }
        }
      });
    }

    // do this later
    setTimeout(() => {
      // mark as read if scroll is at end
      if (clientHeight + scrollTop === scrollHeight) {
        messagesDispatcher({
          type: messageActionTypes.MARK_AS_READ,
        });
        currentGroupChannel.markAsRead();
      }
    }, 500);
  }

  render() {
    const {
      userId,
      disabled,
      scrollRef,
      readStatus,
      membersMap,
      allMessages,
      useReaction,
      emojiAllMap,
      editDisabled,
      deleteMessage,
      updateMessage,
      resendMessage,
      renderCustomMessage,
      renderChatItem,
      highLightedMessageId,
      emojiContainer,
      toggleReaction,
      useMessageGrouping,
      currentGroupChannel,
      memoizedEmojiListItems,
      showScrollBot,
      onClickScrollBot,
    } = this.props;

    if (allMessages.length < 1) {
      return (
        <PlaceHolder
          className="sendbird-conversation__no-messages"
          type={PlaceHolderTypes.NO_MESSAGES}
        />
      );
    }

    return (
      <div className="sendbird-conversation__messages">
        <div
          ref={scrollRef}
          className="sendbird-conversation__scroll-container"
          onScroll={this.onScroll}
        >
          <div className="sendbird-conversation__padding" />
          {/*
            To do: Implement windowing
            Implement windowing if you are dealing with large number of messages/channels
            https://github.com/bvaughn/react-window -> recommendation
            We hesitate to bring one more dependency to our library,
            we are planning to implement it inside the library
          */}
          <div className="sendbird-conversation__messages-padding">
            {
              allMessages.map(
                (m, idx) => {
                  const previousMessage = allMessages[idx - 1];
                  const nextMessage = allMessages[idx + 1];
                  const [chainTop, chainBottom] = useMessageGrouping
                    ? compareMessagesForGrouping(previousMessage, m, nextMessage)
                    : [false, false];
                  const previousMessageCreatedAt = previousMessage && previousMessage.createdAt;
                  const currentCreatedAt = m.createdAt;
                  // https://stackoverflow.com/a/41855608
                  const hasSeperator = !(previousMessageCreatedAt && (
                    isSameDay(currentCreatedAt, previousMessageCreatedAt)
                  ));
                  if (renderChatItem) {
                    return (
                      <div
                        key={m.messageId || m.reqId}
                        className="sendbird-msg--scroll-ref"
                      >
                        {
                          renderChatItem({
                            message: m,
                            highLightedMessageId,
                            channel: currentGroupChannel,
                            onDeleteMessage: deleteMessage,
                            onUpdateMessage: updateMessage,
                            onResendMessage: resendMessage,
                            emojiContainer,
                            chainTop,
                            chainBottom,
                          })
                        }
                      </div>
                    );
                  }

                  return (
                    <MessageHOC
                      highLightedMessageId={highLightedMessageId}
                      renderCustomMessage={renderCustomMessage}
                      key={m.messageId || m.reqId}
                      userId={userId}
                      status={readStatus[m.messageId] || getParsedStatus(m, currentGroupChannel)}
                      // show status for pending/failed messages
                      message={m}
                      currentGroupChannel={currentGroupChannel}
                      disabled={disabled}
                      membersMap={membersMap}
                      chainTop={chainTop}
                      useReaction={useReaction}
                      emojiAllMap={emojiAllMap}
                      editDisabled={editDisabled}
                      hasSeperator={hasSeperator}
                      chainBottom={chainBottom}
                      updateMessage={updateMessage}
                      deleteMessage={deleteMessage}
                      resendMessage={resendMessage}
                      toggleReaction={toggleReaction}
                      memoizedEmojiListItems={memoizedEmojiListItems}
                    />
                  );
                },
              )
            }
          </div>
        </div>
        {
          showScrollBot && (
            <div
              className="sendbird-conversation__scroll-bottom-button"
              onClick={onClickScrollBot}
              onKeyDown={onClickScrollBot}
              tabIndex={0}
              role="button"
            >
              <Icon
                width="24px"
                height="24px"
                type={IconTypes.CHEVRON_DOWN}
                fillColor={IconColors.CONTENT}
              />
            </div>
          )
        }
      </div>
    );
  }
}

ConversationScroll.propTypes = {
  // https://stackoverflow.com/a/52646941
  scrollRef: PropTypes.shape({
    current: PropTypes.oneOfType([
      PropTypes.element,
      PropTypes.shape({}),
    ]),
  }).isRequired,
  hasMore: PropTypes.bool,
  messagesDispatcher: PropTypes.func.isRequired,
  onScroll: PropTypes.func,
  onScrollDown: PropTypes.func,
  editDisabled: PropTypes.bool,
  disabled: PropTypes.bool,
  userId: PropTypes.string,
  allMessages: PropTypes.arrayOf(PropTypes.shape({
    createdAt: PropTypes.number,
  })).isRequired,
  deleteMessage: PropTypes.func.isRequired,
  resendMessage: PropTypes.func.isRequired,
  updateMessage: PropTypes.func.isRequired,
  readStatus: PropTypes.shape({}).isRequired,
  currentGroupChannel: PropTypes.shape({
    markAsRead: PropTypes.func,
    members: PropTypes.arrayOf(PropTypes.shape({})),
  }).isRequired,
  highLightedMessageId: PropTypes.oneOfType([
    PropTypes.string,
    PropTypes.number,
  ]),
  renderChatItem: PropTypes.element,
  renderCustomMessage: PropTypes.func,
  useReaction: PropTypes.bool,
  showScrollBot: PropTypes.bool,
  onClickScrollBot: PropTypes.func,
  emojiContainer: PropTypes.shape({}),
  emojiAllMap: PropTypes.instanceOf(Map),
  membersMap: PropTypes.instanceOf(Map),
  useMessageGrouping: PropTypes.bool,
  toggleReaction: PropTypes.func,
  memoizedEmojiListItems: PropTypes.func,
};

ConversationScroll.defaultProps = {
  hasMore: false,
  editDisabled: false,
  disabled: false,
  userId: '',
  renderCustomMessage: null,
  renderChatItem: null,
  highLightedMessageId: null,
  onScroll: null,
  onScrollDown: null,
  useReaction: true,
  emojiContainer: {},
  showScrollBot: false,
  onClickScrollBot: () => {},
  emojiAllMap: new Map(),
  membersMap: new Map(),
  useMessageGrouping: true,
  toggleReaction: () => { },
  memoizedEmojiListItems: () => '',
};

import React, { useContext } from 'react';
import PropTypes from 'prop-types';

import './notification.scss';
import { LocalizationContext } from '../../../lib/LocalizationContext';
import Label, { LabelTypography, LabelColors } from '../../../ui/Label';
import Icon, { IconTypes, IconColors } from '../../../ui/Icon';

export default function Notification({
  count,
  time,
  onClick,
}) {
  const { stringSet } = useContext(LocalizationContext);
  const timeArray = time.split(' ');
  timeArray.splice(-2, 0, stringSet.CHANNEL__MESSAGE_LIST__NOTIFICATION__ON);
  return (
    // eslint-disable-next-line
    <div className="sendbird-notification" onClick={onClick}>
      <Label className="sendbird-notification__text" color={LabelColors.ONCONTENT_1} type={LabelTypography.CAPTION_2}>
        {`${count} `}
        {stringSet.CHANNEL__MESSAGE_LIST__NOTIFICATION__NEW_MESSAGE}
        {` ${timeArray.join(' ')}`}
      </Label>
      <Icon
        width="24px"
        height="24px"
        type={IconTypes.CHEVRON_DOWN}
        fillColor={IconColors.CONTENT}
      />
    </div>
  );
}

Notification.propTypes = {
  count: PropTypes.oneOfType([
    PropTypes.string,
    PropTypes.number,
  ]),
  time: PropTypes.string,
  onClick: PropTypes.func.isRequired,
};

Notification.defaultProps = {
  count: 0,
  time: '',
};

import React, { useContext } from 'react';

import './notification.scss';

