import React, { useEffect, useState, useContext } from 'react';
import PropTypes from 'prop-types';

import { LocalizationContext } from '../../../lib/LocalizationContext';
import { uuidv4 } from '../../../utils/uuid';
import Label, { LabelTypography, LabelColors } from '../../../ui/Label';

const TypingIndicatorText = ({ members }) => {
  const { stringSet } = useContext(LocalizationContext);
  if (!members || members.length === 0) {
    return '';
  }

  if (members && members.length === 1) {
    return `${members[0].nickname} ${stringSet.TYPING_INDICATOR__IS_TYPING}`;
  }

  if (members && members.length === 2) {
    return `${members[0].nickname} ${stringSet.TYPING_INDICATOR__AND} ${members[1].nickname} ${stringSet.TYPING_INDICATOR__ARE_TYPING}`;
  }

  return stringSet.TYPING_INDICATOR__MULTIPLE_TYPING;
};

function TypingIndicator({ channelUrl, sb, logger }) {
  const [handlerId, setHandlerId] = useState(uuidv4());
  const [typingMembers, setTypingMembers] = useState([]);

  useEffect(() => {
    if (sb && sb.ChannelHandler) {
      sb.removeChannelHandler(handlerId);
      const newHandlerId = uuidv4();
      const handler = new sb.ChannelHandler();
      // there is a possible warning in here - setState called after unmount
      handler.onTypingStatusUpdated = (groupChannel) => {
        logger.info('Channel > Typing Indicator: onTypingStatusUpdated', groupChannel);
        const members = groupChannel.getTypingMembers();
        if (groupChannel.url === channelUrl) {
          setTypingMembers(members);
        }
      };
      sb.addChannelHandler(newHandlerId, handler);
      setHandlerId(newHandlerId);
    }

    return () => {
      setTypingMembers([]);
      if (sb && sb.removeChannelHandler) {
        sb.removeChannelHandler(handlerId);
      }
    };
  }, [channelUrl]);

  return (
    <Label type={LabelTypography.CAPTION_2} color={LabelColors.ONBACKGROUND_2}>
      <TypingIndicatorText members={typingMembers} />
    </Label>
  );
}

TypingIndicator.propTypes = {
  channelUrl: PropTypes.string.isRequired,
  sb: PropTypes.shape({
    ChannelHandler: PropTypes.func,
    removeChannelHandler: PropTypes.func,
    addChannelHandler: PropTypes.func,
  }).isRequired,
  logger: PropTypes.shape({
    info: PropTypes.func,
  }).isRequired,
};

export default TypingIndicator;

// Logic required to handle message input rendering

import React, { useContext } from 'react';
import Sendbird from 'sendbird';

import { RenderGroupChannelMessageInputProps } from '../../../index';
import * as utils from '../utils.js';

import MessageInput from '../../../ui/MessageInput';
import { LocalizationContext } from '../../../lib/LocalizationContext';

interface Props {
  channel: Sendbird.GroupChannel;
  user: Sendbird.User;
  isOnline: boolean;
  initialized: boolean;
  onSendMessage(): void;
  onFileUpload(): void;
  renderMessageInput(renderProps: RenderGroupChannelMessageInputProps): JSX.Element;
}

const MessageInputWrapper = ({
  channel,
  user,
  onSendMessage,
  onFileUpload,
  renderMessageInput,
  isOnline,
  initialized,
}: Props, ref: React.RefObject<HTMLInputElement>): JSX.Element => {
  const { stringSet } = useContext(LocalizationContext);
  const disabled = !initialized
    || utils.isDisabledBecauseFrozen(channel)
    || utils.isDisabledBecauseMuted(channel)
    || !isOnline;

  const isOperator = utils.isOperator(channel);
  const { isBroadcast } = channel;

  // custom message
  if (renderMessageInput) {
    return renderMessageInput({ channel, user, disabled });
  }

  // broadcast channel + not operator
  if (isBroadcast && !isOperator) {
    return null;
  }

  // other conditions
  return (
    <MessageInput
      placeholder={(utils.isDisabledBecauseFrozen(channel)
        && stringSet.CHANNEL__MESSAGE_INPUT__PLACE_HOLDER__DISABLED)
        || (utils.isDisabledBecauseMuted(channel)
          && stringSet.CHANNEL__MESSAGE_INPUT__PLACE_HOLDER__MUTED
        )
      }
      ref={ref}
      disabled={disabled}
      onStartTyping={() => {
        channel.startTyping();
      }}
      onSendMessage={onSendMessage}
      onFileUpload={onFileUpload}
    />
  );
}

export default React.forwardRef(MessageInputWrapper);

