export const SENDING_MESSAGE_START = 'SENDING_MESSAGE_START';
export const RESENDING_MESSAGE_START = 'RESENDING_MESSAGE_START';
export const FETCH_PARTICIPANT_LIST = 'FETCH_PARTICIPANT_LIST';
